DROP TABLE IF EXISTS `books`;
CREATE TABLE `books` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `bookname` char(50) NOT NULL DEFAULT '',
  `publisher` char(80) NOT NULL DEFAULT '',
  `author` char(30) NOT NULL DEFAULT '',
  `price` decimal(10,2) NOT NULL DEFAULT '0.00',
