INSERT INTO `intro` VALUES ('1', '<p><span style=\"font-family:arial, helvetica, sans-serif;font-size:12px;line-height:19px;text-align:justify;widows:auto;background-color:#ffffff;\">wei图书馆（bookManage.com）坐落于繁华的河西区西南楼中环线上，地理位置优越，以“自主创 业，真诚服务，坦诚做事，满足需求” 为书店的经营理念。书店周边有众多重点中、小学校，各类学生课本、学习辅<img src=\"/bookmanage4.0/editor/php/../../attachment/images/20150507/14309280163126.gif\" title=\"about.gif\" style=\"float:right;\" />导、工具用书是书店销售的重点。松坡书店把顾客的需求当成自己的职责，为了方便顾客购书，延长了书店的营业时间；为使书店不断上升水平，常丽霞从抓形象素质入手，制定了包退包换，配送和代购邮寄等业务，建立了一系列书店的管理制度、服务承诺以及责任制，赢得了广大顾客的信赖和赞誉。 超逸书店的经营者常丽霞，原是一名普通下岗职工，凭借自强自立的拼搏精神，走上了自谋职业的道路，通过艰苦的努力办起了超逸书店，成了再就业明星。常丽霞通过参加妇女创业中心SYB培训，掌握了创业知识和技能、提高了综合素质、减少了创办企业的盲目性、增强了融资能力；参加妇女创业中心举办的ICT电子商务培训，使其了解了信息技术和商务规则知识，掌握了用网页的方式在网上发布商品及其他信息，和在网上做广告等。通过展示，书店可以树立自己的企业形象，扩大知名度，宣传产品和服务。建立超逸网络书店，常丽霞开展了各类信息化网络服务业务，通过网上支付系统，书店服务的范围延伸到全国乃至世界各个地区的读者们。 有耕耘就有收获，2005年，常丽霞参加了妇女创业中心举办的创业计划书大赛，并获得了大赛二等奖，得到了多方面的高度评价。市里领导也曾多次光顾书店指导，常丽霞光荣的出席了市第十一届妇女代表大会，并被授予市十五立功奖章、区三八红旗手和创业明星、优秀共产党员称号。</span><br /></p>');

-- ----------------------------
-- Table structure for user
-- ----------------------------
DROP TABLE IF EXISTS `user`;
CREATE TABLE `user` (
  `u_id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `u_name` varchar(20) DEFAULT NULL,
  `u_realname` varchar(20) DEFAULT NULL,
  `u_pass` varchar(255) DEFAULT NULL,
  `u_login_time` int(11) DEFAULT NULL,
  `u_status` tinyint(1) DEFAULT NULL,
  `u_ip` varchar(255) DEFAULT NULL,
  `u_email` varchar(255) DEFAULT NULL,
  PRIMARY KEY (`u_id`)
) ENGINE=InnoDB AUTO_INCREMENT=18 DEFAULT CHARSET=utf8;

